oxy_webhelp_navlinks = {"feature_list_with_all_features_intro-d43829e11863":{"topics":[{"title":"Componentisation of listeners","shortdesc":"<p class=\"shortdesc\">The structure of components in the Product libray was         extended to support the characteristics of listeners.  <\/p>","href":"modules\/cleva_workflow\/feature\/code_139582_en\/code_139582.html","attributes":{"data-id":"code_139582"},"menu":{"hasChildren":false},"tocID":"code_139582-d43829e11878","topics":[]},{"title":"Redesign of the widget configuration screen","shortdesc":"<p class=\"shortdesc\">The widgets page in Framework configurator, to configure         the widgets that are displayed for tasks and processes in the Task manager, was modified for         a better user experience.  <\/p>","href":"modules\/cleva_workflow\/feature\/code_152306_en\/code_152306.html","attributes":{"data-id":"code_152306"},"menu":{"hasChildren":false},"tocID":"code_152306-d43829e11896","topics":[]},{"title":"Communication between widgets in the Task manager","shortdesc":"<p class=\"shortdesc\">This feature allows widgets to adapt their behaviour         depending on shared information that is available for all widgets.<\/p>","href":"modules\/cleva_workflow\/feature\/code_154444_en\/code_154444.html","attributes":{"data-id":"code_154444"},"menu":{"hasChildren":false},"tocID":"code_154444-d43829e11914","topics":[]},{"title":"Cleva Widgets catalogue's translations","shortdesc":"<p class=\"shortdesc\">This feature allows the translation of information         regarding Cleva widgets.<\/p>","href":"modules\/cleva_workflow\/feature\/code_176676_en\/code_176676.html","attributes":{"data-id":"code_176676"},"menu":{"hasChildren":false},"tocID":"code_176676-d43829e11932","topics":[]},{"title":"Data cleanup","shortdesc":"<p class=\"shortdesc\">This feature allows automatically deleting old information         about processes.<\/p>","href":"modules\/cleva_workflow\/feature\/code_176999_en\/code_176999.html","attributes":{"data-id":"code_176999"},"menu":{"hasChildren":false},"tocID":"code_176999-d43829e11950","topics":[]},{"title":"New process category: Surrenders","shortdesc":"<p class=\"shortdesc\">A new item was added to the list of available categories         to allow categorising and filtering surrender processes. The name of this new category is         Surrenders.<\/p>","href":"modules\/cleva_workflow\/feature\/code_176309_en\/code_176309.html","attributes":{"data-id":"code_176309"},"menu":{"hasChildren":false},"tocID":"code_176309-d43829e11968","topics":[]},{"title":"Multichannel support in the BAM component and in operational dashboards","shortdesc":"<p class=\"shortdesc\">The Business Activity Monitoring (BAM) component and         operational dashboards for tasks and SLA managements were updated to support the filter of         information by channel.<\/p>","href":"modules\/cleva_workflow\/feature\/code_158139_en\/code_158139.html","attributes":{"data-id":"code_158139"},"menu":{"hasChildren":false},"tocID":"code_158139-d43829e11986","topics":[]},{"title":"Sort results by deadline when searching for processes","shortdesc":"<p class=\"shortdesc\">The process search results are now sorted by deadline, by         default.<\/p>","href":"modules\/cleva_workflow\/feature\/code_152424_en\/code_152424.html","attributes":{"data-id":"code_152424"},"menu":{"hasChildren":false},"tocID":"code_152424-d43829e12004","topics":[]},{"title":"Standardisation of table styles in Framework configurator ","shortdesc":"<p class=\"shortdesc\">To align styles in all screens and to promote a better         user experience, the action buttons in the tables that are displayed in Framework         configurator were changed into icons.. <\/p>","href":"modules\/cleva_workflow\/feature\/code_150786_en\/code_150786.html","attributes":{"data-id":"code_150786"},"menu":{"hasChildren":false},"tocID":"code_150786-d43829e12022","topics":[]},{"title":"Comments on tasks","shortdesc":"<p class=\"shortdesc\">A new widget is available to register and view comments in         a task.<\/p>","href":"modules\/cleva_workflow\/feature\/code_145075_en\/code_145075.html","attributes":{"data-id":"code_145075"},"menu":{"hasChildren":false},"tocID":"code_145075-d43829e12040","topics":[]},{"title":"Distinguish process by identification document","shortdesc":"<p class=\"shortdesc\">This feature modified the searches of Processes and Tasks         in the Task manager to seek elements by the identification document value of the party         instead of the tax id.<\/p>","href":"modules\/cleva_workflow\/feature\/code_177131_en\/code_177131.html","attributes":{"data-id":"code_177131"},"menu":{"hasChildren":false},"tocID":"code_177131-d43829e12059","topics":[]},{"title":"Configuration of process access out of the flow definition","shortdesc":"<p class=\"shortdesc\">This feature allows configuring users and groups of users that have permissions to instanciate or access processes in a configuration screen.<\/p>","href":"modules\/cleva_workflow\/feature\/code_156189_en\/code_156189.html","attributes":{"data-id":"code_156189"},"menu":{"hasChildren":false},"tocID":"code_156189-d43829e12077","topics":[]},{"title":"Automatic widget configurations","shortdesc":"<p class=\"shortdesc\">This feature allows creating configurations of widgets for processes using either the Cleva base configurations or a new mechanism for importing and exporting configurations.<\/p>","href":"modules\/cleva_workflow\/feature\/code_154451_en\/code_154451.html","attributes":{"data-id":"code_154451"},"menu":{"hasChildren":false},"tocID":"code_154451-d43829e12095","topics":[]},{"title":"<em class=\"ph i\">Ad hoc <\/em>configuration of widgets in the Task manager","shortdesc":"<p class=\"shortdesc\">This feature allows controlling the behaviour of a widget in the Task manager. <\/p>","href":"modules\/cleva_workflow\/feature\/code_149094_en\/code_149094.html","attributes":{"data-id":"code_149094"},"menu":{"hasChildren":false},"tocID":"code_149094-d43829e12113","topics":[]},{"title":"Registration of Cleva Workflow jobs in the new batch console","shortdesc":"<p class=\"shortdesc\">A new batch console was implemented for all Cleva modules         in release 7.11.  <\/p>","href":"modules\/cleva_workflow\/feature\/code_159803_en\/code_159803.html","attributes":{"data-id":"code_159803"},"menu":{"hasChildren":false},"tocID":"code_159803-d43829e12133","topics":[]},{"title":"Cleva process templates and components in Modeller","shortdesc":"<p class=\"shortdesc\">The Modeller has been extended to support Cleva process         templates and components.<\/p>","href":"modules\/cleva_workflow\/feature\/code_149106_en\/code_149106.html","attributes":{"data-id":"code_149106"},"menu":{"hasChildren":false},"tocID":"code_149106-d43829e12151","topics":[]}]}};