oxy_webhelp_navlinks = {"process_management-d43829e35035":{"topics":[{"title":"Task manager - General features: Processes","shortdesc":"<p class=\"shortdesc\">You can search business processes using several criteria.<\/p>","href":"modules\/cleva_workflow\/feature\/code_37628_en\/code_37628_functional.html","attributes":{"data-id":"code_37628_functional"},"menu":{"hasChildren":false},"tocID":"code_37628_functional-d43829e35053","topics":[]},{"title":"Support cases in the process search screen","shortdesc":"<p class=\"shortdesc\">The process search screen in the task manager now allows         searching processes in both BPMN and CMMN standards. Therefore, there is no need for a         specific menu, and the Group life acceptance menu was removed.<\/p>","href":"modules\/cleva_workflow\/feature\/code_121738_en\/code_121738_functional.html","attributes":{"data-id":"code_121738_functional"},"menu":{"hasChildren":false},"tocID":"code_121738_functional-d43829e35071","topics":[]},{"title":"Sort results by deadline when searching for processes","shortdesc":"<p class=\"shortdesc\">The process search results are now sorted by deadline, by         default.<\/p>","href":"modules\/cleva_workflow\/feature\/code_152424_en\/code_152424_functional_2.html","attributes":{"data-id":"code_152424_functional"},"menu":{"hasChildren":false},"tocID":"code_152424_functional-d43829e35096","topics":[]},{"title":"Configuration of process access out of the flow definition","shortdesc":"<p class=\"shortdesc\">This feature allows configuring users and groups of users that have permissions to instanciate or access processes in a configuration screen.<\/p>","href":"modules\/cleva_workflow\/feature\/code_156189_en\/code_156189_functional_2.html","attributes":{"data-id":"code_156189_functional"},"menu":{"hasChildren":false},"tocID":"code_156189_functional-d43829e35114","topics":[]}]}};