oxy_webhelp_navlinks = {"widgets-d43829e34481":{"topics":[{"title":"Document management widget integration","shortdesc":"<p class=\"shortdesc\">This feature allows the integration of the CMS widget for         document management within processes and tasks in the task manager.<\/p>","href":"modules\/cleva_workflow\/feature\/code_100303_en\/code_100303_functional.html","attributes":{"data-id":"code_100303_functional"},"menu":{"hasChildren":false},"tocID":"code_100303_functional-d43829e34496","topics":[]},{"title":"Generic widgets","shortdesc":"<p class=\"shortdesc\">This feature provides the structure and interface to         implement widgets for the product library, which may be used in both Cleva Workflow’s         processes and Cleva Frontend factory’s pages.<\/p>","href":"modules\/cleva_workflow\/feature\/code_101624_en\/code_101624_functional.html","attributes":{"data-id":"code_101624_functional"},"menu":{"hasChildren":false},"tocID":"code_101624_functional-d43829e34521","topics":[]},{"title":"Workflow widgets compatibilisation","shortdesc":"<p class=\"shortdesc\">This feature introduces several widgets, available to be used in either         any process or task in the task manager or in a page of Cleva Frontend factory<\/p>","href":"modules\/cleva_workflow\/feature\/code_109708_en\/code_109708_functional.html","attributes":{"data-id":"code_109708_functional"},"menu":{"hasChildren":false},"tocID":"code_109708_functional-d43829e34546","topics":[]},{"title":"Pagination in comments","shortdesc":"<p class=\"shortdesc\">This feature introduces a performance improvement in the         processes' comments feature by using pagination.<\/p>","href":"modules\/cleva_workflow\/feature\/code_100208_en\/code_100208_functional.html","attributes":{"data-id":"code_100208_functional"},"menu":{"hasChildren":false},"tocID":"code_100208_functional-d43829e34571","topics":[]},{"title":"Cleva Widgets catalogue's translations","shortdesc":"<p class=\"shortdesc\">This feature allows the translation of information         regarding Cleva widgets.<\/p>","href":"modules\/cleva_workflow\/feature\/code_176676_en\/code_176676_functional_2.html","attributes":{"data-id":"code_176676_functional"},"menu":{"hasChildren":false},"tocID":"code_176676_functional-d43829e34596","topics":[]},{"title":"Automatic widget configurations","shortdesc":"<p class=\"shortdesc\">This feature allows creating configurations of widgets for processes using either the Cleva base configurations or a new mechanism for importing and exporting configurations.<\/p>","href":"modules\/cleva_workflow\/feature\/code_154451_en\/code_154451_functional_2.html","attributes":{"data-id":"code_154451_functional"},"menu":{"hasChildren":false},"tocID":"code_154451_functional-d43829e34614","topics":[]}]}};