oxy_webhelp_navlinks = {"workflow-d43829e34315":{"topics":[{"title":"Multi processes parallel execution","shortdesc":"<p class=\"shortdesc\">This feature provides a mechanism to allow opening several         processes simultaneously. <\/p>","href":"modules\/cleva_workflow\/feature\/code_13157_en\/code_13157_functional.html","attributes":{"data-id":"code_13157_functional"},"menu":{"hasChildren":false},"tocID":"code_13157_functional-d43829e34330","topics":[]},{"title":"Update process and task variables","shortdesc":"<p class=\"shortdesc\">There are three new web services that allow updating         variables in an open process, case and task, so that it is possible to correct the values of         variables after creation.<\/p>","href":"modules\/cleva_workflow\/feature\/code_114638_en\/code_114638_functional.html","attributes":{"data-id":"code_114638_functional"},"menu":{"hasChildren":false},"tocID":"code_114638_functional-d43829e34355","topics":[]},{"title":"Data cleanup","shortdesc":"<p class=\"shortdesc\">This feature allows automatically deleting old information         about processes.<\/p>","href":"modules\/cleva_workflow\/feature\/code_176999_en\/code_176999_functional_2.html","attributes":{"data-id":"code_176999_functional"},"menu":{"hasChildren":false},"tocID":"code_176999_functional-d43829e34380","next":"code_176999_functional-d43829e34380"},{"title":"Hierarchy","shortdesc":"<p class=\"shortdesc\">This feature provides a structure to support a         hierarchical organisation of groups and users within the workflow. Additionally, a button         was implemented to (re)assign tasks, what allows to escalate and delegate tasks using the         hierarchy.<\/p>","href":"modules\/cleva_workflow\/feature\/code_100232_en\/code_100232_functional.html","attributes":{"data-id":"code_100232_functional"},"menu":{"hasChildren":false},"tocID":"code_100232_functional-d43829e34416","topics":[]}]}};